const theUrl = "https://script.google.com/macros/s/AKfycbwg46p8shI9z5uGQV33n-DP-Mgblo2zgUb969NmeYbmAnThnA/exec";
// Funzioni
function clearStorage() {
    window.parent.postMessage('{"name":"clearStorage"}', "*");
}
function getStorage() {
    window.parent.postMessage('{"name":"getStorage"}', "*");
    return new Promise((resolve) => {
        function rsm(event) {
            var r = JSON.parse(event.data);
            if (r.name == "storage") {
                console.log("storage: " + r.value);
                window.removeEventListener("message", rsm);
                resolve(r.value);
            }
        }
        window.addEventListener("message", rsm);
    });
}
function setStorage(val) {
    var d = {
        "name": "setStorage",
        "value": val
    };
    window.parent.postMessage(JSON.stringify(d), "*");
}

var score = runtimeScene.getGame().getVariables().get("Score").getAsNumber();
var record = runtimeScene.getGame().getVariables().get("Highscore").getAsNumber();
var key = runtimeScene.getGame().getVariables().get("Key").getAsString();

function setScore(scorePass) {
    var strg = getStorage();
    strg.then(value => {
        try {
            if (value != null) {
                var ts = JSON.parse(atob(value));
                if (scorePass > ts.s) ts.s = scorePass;
            } else {
                var ts = {
                    s: score
                };
            }
            setStorage(btoa(JSON.stringify(ts)));
        } catch (e) {
            clearStorage();
        }
    });
}
if (score > record)
    setScore(score);
if (key) setTimeout(function () {
    var xmlHttp = new XMLHttpRequest();
    xmlHttp.onreadystatechange = function () {
        if (xmlHttp.readyState == 4 && xmlHttp.status == 200) {
            var risposta = JSON.parse(atob(xmlHttp.responseText));
            if (!risposta.r && risposta.e != "lock") {
                console.log("Errore invio punteggio");
                clearStorage();
                setTimeout(function () {
                    runtimeScene.getVariables().get("reset").setNumber(1);
                }, 100);
            } else {
                setScore(risposta.s);
            }
        }
    }
    var body = {
        "h": "p",
        "s": score,
        "key": key
    };
    xmlHttp.open("POST", theUrl, true);
    xmlHttp.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
    xmlHttp.send(btoa(JSON.stringify(body)));
}, 20);

