window.addEventListener("message", function (event) {
    var message = JSON.parse(event.data);
    if (message.name == "storage") {
        var key = "";
        var highscore = 0;
        var username = "";
        if (message.value) {
            try {
                var dec = JSON.parse(atob(message.value));
                highscore = dec.s;
                username = dec.n;
                key = dec.key;
            } catch (e) { }
        }
        runtimeScene.getGame().getVariables().get("Highscore").setNumber(highscore);
        runtimeScene.getGame().getVariables().get("Username").setString(username);
        runtimeScene.getGame().getVariables().get("Key").setString(key);
    } else if (message.name == "chiudiSettings2")
        runtimeScene.getVariables().get("reset").setNumber(1);
});

